typedef long long       ll;

class Solution {
public:
    long long minimumCost(string s) {               int n = s.size();   s = "#" + s;

        ll  f[2][2][2];
        memset(f, 0x3f, sizeof(f));
        f[0][0][0] = 0;
        f[0][1][0] = 0;

        int x = 0, w = 0;
        for (int i = 1; i <= n; i++) {                  x ^= 1;
            for (int t = 0; t <= 1; t++) {              w = t ^ (s[i]-'0');                       
                f[x][w][t]      = min(f[x][w][t],       f[1-x][w][t]);                  // Decision 1
                f[x][w^1][t]    = min(f[x][w^1][t],     f[1-x][w][t] + i);              // Decision 2
                f[x][w^1][t^1]  = min(f[x][w^1][t^1],   f[1-x][w^1][t] + (n+1-i));      // Decision 3
                f[x][w][t^1]    = min(f[x][w][t^1],     f[1-x][w][t] + (n+1));          // Decision 4
            }
            memset(f[1-x], 0x3f, sizeof(f[1-x]));
        }

        ll  ans = LLONG_MAX;
        for (int w = 0; w <= 1; w++) {
            for (int t = 0; t <= 1; t++) ans = min(ans, f[x][w][t]);
        }
        return ans;
    }
};

```

