class Solution {
private:
    long long help(string s,char ch){
        long long ans=0,i;
        int n=s.size();
        bool correct=true;
//first half
        for(i=n/2-1;i>-1;--i){
            if(correct&&s[i]!=ch){
                ans+=i+1;
                correct=false;
            }
            else if(!correct&&s[i]==ch){
                ans+=i+1;
                correct=true;
            }
        }
//correct is used so that we dont have to invert all the 0s and 1s 
//everytime we apply an operation
        long long ans2=0;
        correct=true;
        int x=s.size()/2;
        if(x&1)++x;
//second half
        for(i=n/2;i<n;++i){
            if(correct&&s[i]!=ch){
                ans2+=n-i;
                correct=false;
            }
            else if(!correct&&s[i]==ch){
                ans2+=n-i;
                correct=true;
            }
        }

        return ans+ans2;

    }
public:
    long long minimumCost(string s) {
        long long ans=LLONG_MAX;
        ans=min(ans,help(s,'0'));
        ans=min(ans,help(s,'1'));

        return ans;
    }
};
```

