        StringBuilder sb = new StringBuilder();
        sb.append(n).append(2 * n).append(3 * n);
        if (sb.length() != 9) return false;
        int state = 0;
        for (int i = 0; i < sb.length(); i++) {
            int c = sb.charAt(i) - '0';
            if (c == 0 || ((state >> c) & 1) == 1) return false;
            state |= (1 << c); 
        }
        return true;
    }
}
