class Solution {
public:
    bool isFascinating(int n) {
        int arr[10]{};
        int d = n*2;
        int t = n*3;
        while(t)
        {
            arr[d%10]++;
            arr[n%10]++;
            arr[t%10]++;
            n/=10;
            d/=10;
            t/=10;
        }
        for(int i=1;i<10;i++)
        {
              if(arr[i]!=1)
                return false;
        }
        return true;
    }
};
