     * @param Integer $n
     * @return Boolean
     */
    function isFascinating($n) {
        $num1 = $n * 2;
        $num2 = $n * 3;
        $n = "{$n}{$num1}{$num2}";
        $carry = [];

        for($i = 0, $ln = strlen($n); $i < $ln; $i++) {
            $digit = $n[$i];

            if ($digit == 0 || in_array($digit, $carry)) {
                return false;
            }

            $carry[] = $digit;
        }

        if (count($carry) !== 9) {
            return false;
        }

        return true;
    }
}
