class Solution {
    public boolean isFascinating(int n) {   
        StringBuilder stb = new StringBuilder();
        stb.append(n).append(2 * n).append(3 * n);
        return isFascinating(stb.toString());
    }

    private boolean isFascinating(String s) {
        if (s.length() != 9) {
            return false;
        }
        int[] digitCount = new int[10];
        char ch;
        for (int i = 0; i < s.length(); i++) {
            ch = s.charAt(i);
            if (ch == '0' || ++digitCount[ch - '0'] > 1) {
                return false;
            }
        }
        return true;
    }
}
