class Solution {
    public boolean isFascinating(int n) {

        int twoN = 2 * n;
        int threeN = 3 * n;

        // analyze numbers for fascinating-ness
        int[] counter = new int[10];
        while (n > 0) {
            ++counter[n % 10];
            n /= 10;
        }
        while (twoN > 0) {
            ++counter[twoN % 10];
            twoN /= 10;
