var isFascinating = function(n) {
    let nums = '';
    n = n.toString()
    for(let i=1; i<=n.length; i++){
        nums += parseInt(n)*i
    }
    if(nums.length > 9) return false
    let finSet = new Set(nums.split(''))
    if(finSet.has('0')) return false
    return finSet.size === 9
};
