function distanceTraveled(mainTank: number, additionalTank: number): number {
    if (additionalTank === 0) return mainTank * 10
    let t = 0;
    while (mainTank > 0) {
        if (mainTank - 5 >= 0 && additionalTank > 0) {
            t += 5;    
            mainTank -= 4;
            additionalTank--;
        } else {
            t += mainTank;
            mainTank = 0;
        }
    }
    return t * 10;
};
```

