object Solution {
  def distanceTraveled(mainTank: Int, additionalTank: Int): Int = {
    eval(0, mainTank, additionalTank) { case (d, m, a) =>
      if (m < 5 || a == 0) Right(d + m * 10)
      else Left(d + 50, m - 4, a - 1)
    }
  }

  def eval[A, B](init: A)(f: A => Either[A, B]): B = 
   LazyList.unfold(Option(init)) { 
     _.flatMap(f(_).fold(
       x => Option(None -> Some(x)), 
       x => Option(Some(x) -> None)
     ))
   }.flatten.head
}
