class Solution {
    public int distanceTraveled(int mainTank, int additionalTank) {
        if(mainTank < 5) return mainTank * 10;

        int totalFuel = 0;
        while(mainTank > 0) {
            if(mainTank >= 5 && additionalTank >= 1) {
                totalFuel += 5;
                mainTank += 1;
                mainTank -= 5;
                additionalTank -= 1;
            } else {
                int temp = mainTank;
                totalFuel += temp;
                mainTank -= temp;
            }
        }

        return totalFuel * 10;
    }
}
