var sortColors = function(nums) {

    const [RED, WHITE, BLUE] = [0, 1, 2];

    let i = 0;
    let [idx_red, idx_blue] = [0, nums.length-1];

    while(i <= idx_blue ){

        if(nums[i] == RED){

            // Swap red element to lefthand side
            [ nums[i], nums[idx_red] ] = [ nums[idx_red], nums[i] ];
            idx_red++;

        }else if(nums[i] == BLUE ){

            // Swap blue element to righthand side
            [ nums[i], nums[idx_blue] ] = [ nums[idx_blue], nums[i] ];
            idx_blue--;

            // i stay here for one more check on next iteration
            i -= 1;
        }


        // i moves from left to right
        i += 1;
    } 

};
