class Solution {
public:
    void sortColors(vector<int>& v) {
        int i, j, k, n = v.size();
        for(i = 0; i < n && v[i] == 0; i++); // Loop from front until first non-zero element
        for(j = n - 1; j >= 0 && v[j] == 2; j--); // Loop from back until first non-two element
		// i -> first non-zero index
		// j -> last non-two index
        for(k = i; k <= j; k++){
            if(v[k] == 0){
				// If 0, swap it with first non-zero index and increment i
                swap(v[k], v[i]);
                i++;
            }
            else if(v[k] == 2){
				// If 2, swap it with last non-two index and decrement j
                swap(v[k], v[j]);
                j--;
                k--; // To not increment k in the next iteration
            }
        }
    }
};
```

