/*

    Time Complexity : O(NlonN), As we are doing sorting. Where N is size of the array(nums).

    Space Complexity: O(1), Constant space.

    Solved using Array + Sorting.

*/


/***************************************** Approach 1 *****************************************/

class Solution {
public:
    void sortColors(vector<int>& nums) {
        sort(nums.begin(), nums.end());
    }
};






/*

    Time Complexity : O(N), As we iterate the array equal to two times. Where N is size of the array(nums).

    Space Complexity: O(1), Constant space.

    Solved using Array + Counting.

*/


/***************************************** Approach 2 *****************************************/

class Solution {
public:
    void sortColors(vector<int>& nums) {
        int nextZeros = 0, nextOnes = 0, nextTwos = 0;
        for(auto it : nums){
            if(it == 0) nextZeros++;
            else if(it == 1) nextOnes++;
            else nextTwos++;
        }
        int k = 0;
        for(int i=0; i<nextZeros; i++) nums[k++] = 0;
        for(int i=0; i<nextOnes; i++) nums[k++] = 1;
        for(int i=0; i<nextTwos; i++) nums[k++] = 2;
    }
};






/*

    Time Complexity : O(N), As we iterate the array(nums) one times. Where N is size of the array(nums).

    Space Complexity: O(1), Constant space.

    Solved using Array + Two Pointers.

*/


/***************************************** Approach 3 *****************************************/

class Solution {
public:
    void sortColors(vector<int>& nums) {
        int nextZeros = 0, nextTwos = nums.size()-1, i = 0;
        while(i <= nextTwos){
            if(nums[i] == 0){
               swap(nums[i], nums[nextZeros]);
               nextZeros++; i++; 
            }
            else if(nums[i] == 2){
                swap(nums[i], nums[nextTwos]);
                nextTwos--;
            }
            else{
                i++;
            }
        }
    }
};
