            int len=nums.size();
            if(len<=1)  return;
            int one=0, two=len-1, zero=0;
            while(one<=two){
                if(nums[one]==0)  swap(nums[one++], nums[zero++]);
                else if (nums[one]==2)  swap(nums[one], nums[two--]);
                else  one++;
            }
