var sortColors = function(nums) {
    let low= 0, high= nums.length- 1, mid= 0, temp= 0;

    while(mid <= high) {
        if(nums[mid] === 0) {
            // swap mid and low pointers values
            temp= nums[low];
            nums[low] = nums[mid];
            nums[mid]= temp;

            low++;
            mid++;
        } else if(nums[mid] === 2) {
            // swap mid and high pointers values
            temp= nums[high];
            nums[high]= nums[mid];
            nums[mid]= temp;

            high--;
        } else {
            mid++
        }
    }

    return nums;
};

