                swap(nums[ptr0],nums[ptr1]);
                ptr0++,ptr1++;
            }
            else if(nums[ptr1] == 1){
                ptr1++;
            }
            else{
                swap(nums[ptr1],nums[ptr2]);
                ptr2--;
            }
        }
