public void sortColors(int[] nums) {
        int l=0;
        int r=nums.length-1;
        while(l<r){
            if(nums[l] == 2) {
                swap(nums, l, r);
                r--;
            }
            else if(nums[l] == 1){
                int temp = l+1;
                while(temp < r && nums[temp] != 0) temp++;
                if(nums[temp] == 0) swap(nums, l, temp);
                l++;
            }
            else l++;
        }
    }
    private void swap(int[] nums, int a, int b){
        int temp = nums[a];
        nums[a] = nums[b];
        nums[b] = temp;
    }
