void merge (vector<int> & v1 , vector <int> & v2 , vector <int> & nums){
    int a  = 0 , b = 0 , k = 0 ; 
    while(a < v1.size() && b < v2.size() ){
        if (v1[a] < v2[b]){
            nums[k++] = v1[a++] ; 
        }
        else nums[k++] = v2[b++] ; 
    }
    if (a == v1.size() ) while(b < v2.size() ) nums[k++] = v2[b++] ; 
    else if ( b == v2.size()) while(a < v1.size() ) nums[k++] = v1[a++] ; 
}

void mergesort (vector <int> & nums ){
    int n = nums.size() ; 
    if (n == 1) return ;  
    int n1 = n / 2 , n2 = n - (n/2) ; 
    vector <int> v1(n1) , v2(n2) ;
    for (int a = 0 ;a < n1 ;a++){
        v1[a] = nums[a] ;
    } 
    for (int b = 0 ;b < n2 ; b++){
        v2[b] = nums[b+n1] ;
    }
    mergesort (v1) ; 
    mergesort(v2) ; 
    merge(v1 ,v2, nums) ; 
    v1.clear () ; 
    v2.clear () ;   
} 
    void sortColors(vector<int>& nums) {

        return mergesort(nums) ; 
