    for i := 0; i<len(nums); i++ {
        if flag0 == false {
            if nums[i] != 0 {
                flag0 = true
                i--
            }
            continue
        }
        if nums[i] == 1 {
            continue
        } else if nums[i] == 0 {
            if i == 0 {
                continue
            }
            nums = append(nums[:i], nums[i+1:]...)
            nums = append([]int{0}, nums...)
            i++
        } else if nums[i] == 2 {
            if i == len(nums)-1 {
                break
            }
            nums = append(nums[:i], nums[i+1:]...)
            nums = append(nums, 2)
            i--
        }
        fmt.Println(nums)
    }
    fmt.Println(nums)
}

