    int hIndex(vector<int>& citations) {
        int n = citations.size();
        int low = 0;
        int high = citations.size()-1;

        while(low <= high){
            int mid = (low + high)/2;
            if(citations[mid] == ((n - mid))){
                return citations[mid];
            }
            else if(citations[mid]  < (n - mid)){
                low = mid +  1;
            }
            else if(citations[mid] > (n-mid)){
                high = mid - 1;
            }

        }
        return n - low;
    }
};
