class Solution {
public:
    int hIndex(vector<int>& a) {

        int n = a.size();

        // using predicate p(x): a[mid] >= n - mid.
        // this will result in FFFFTTTT.
        // I am looking for first T.

        int lo = 0, hi = n - 1;

