import static java.lang.Math.ceil;
import static java.lang.Math.max;
class Solution {
    //already in ascending order
    public int hIndex(int[] citations) {
       return bisectArray(citations, 1, citations.length); 
    }

    public int bisectArray(int[] citations, int start, int end) {
        // terminal case of recursion
        if(start > end) {
           return 0;
        }
        int mid = (int)(start+end)/2;
        int result = 0;

        if(citations[citations.length-mid] >= mid) {
            result = max(mid, bisectArray(citations, mid +1, end));

        } else {
            result = bisectArray(citations, start, mid-1);
        }
        return result;
    }
}
```

