class Solution {
    func hIndex(_ c: [Int]) -> Int {
        var lhs = 0, rhs = c.count
        while lhs < rhs {
            let mid = lhs + (rhs - lhs + 1) >> 1
            c[c.count - mid] >= mid ? (lhs = mid) : (rhs = mid - 1)
        }
        return lhs
    }
}
