class Solution {
public:
    int hIndex(vector<int>& citations) {
        if (citations.empty()) return 0;

        int i = 0;
        int size = citations.size();
        int j = size - 1;

        while (i < j)
        {
            int mid = i + (j - i) / 2;
            int value = citations[mid];
            int hIndex = size - mid;
            if (value == hIndex) return value;
            else if (value > hIndex) j = mid;
            else i = mid + 1;
        }

        if (j < 0 || (citations[j] < (size - j))) return 0;
        return size - i;
    }
};
```

