class Solution {

    boolean count(int[] arr,int i){

        int c=0;
        for(int x:arr){
            if(x>=i){
                c++;
            }
        }

        if(c>=i){
            return true;
        }

        return false;

    }

    public int hIndex(int[] arr) {

        if(arr.length==1){

            return arr[0]>0?1:0;

        }

        int l=0;
        int h=arr[arr.length-1];
        int maxx=0;

        while(l<=h){

            int m=l+(h-l)/2;

            if(count(arr,m)){
                maxx=Math.max(maxx,m);
                l=m+1;
            }
            else{
                h=m-1;
            }

        }

        return maxx;

    }

}
