          hi=mid-1;//as hame upper bound hona hai na agr right me gya to sare bade hi milenge na isliye h=mid-1 kr diya

      }
      else
      {
          lo=mid+1;
      }
    }
    return ans;
}
    int hIndex(vector<int>& citations) {
      int lo=1;
