class Solution:
    def subsets(self, nums: List[int]) -> List[List[int]]:
        from collections import deque

        qu = deque([[]])

        i = 0
        while qu and i < len(nums):
            N = len(qu)

            for _ in range(N):
                lst = qu.popleft()

                qu.append(lst[:])
                qu.append(lst + [nums[i]])


            i += 1

        return list(qu)
