class Solution {
    public List<List<Integer>> subsets(int[] nums) {
         List<List<Integer>> ans = new ArrayList<>();
        if(nums.length==0){
            return ans;
        }
       if(nums.length==1){
           ans.add(0,new ArrayList<>());
           List<Integer> last = new ArrayList<>();
           last.add(nums[0]);
           ans.add(1,last);
           return ans;
       }
       List<List<Integer>> small = subsets(Arrays.copyOfRange(nums,0,nums.length-1));
       for(int i=0;i<small.size();i++){
           ans.add(2*i,small.get(i));
           List<Integer> next = new ArrayList<>();
           for(int k=0;k<small.get(i).size();k++){
               next.add(small.get(i).get(k));
