class Solution {
public:
    int firstBadVersion(int n) {
        long long int low=1, high=n, mid;
        while(low<=high)
        {
            mid=(low+high)/2;
            if(isBadVersion(mid))
            {
                if(mid-1>=0 && !isBadVersion(mid-1))
                {
                    return mid;
                }
                high = mid-1;
            }
            else if(!isBadVersion(mid))
            {
                low = mid+1;
            }
        }
        return mid;
    }
};
