class Solution {
public:
    int firstBadVersion(int n) {
        long low=1;
        long high=n;
        long mid;

        while(low<=high){
            mid=(low+high)/2;

            // if mid is bad
            if(isBadVersion(mid)){

                if(!isBadVersion(mid-1)){ 
                    return mid;
                }
                else high=mid-1;
            }

            //if mid is not BAD
            else{
                low=mid+1;
                //our low will set in mid+1 position
            }
        }
        return mid;
    }
};
