var solution = function(isBadVersion) {
    /**
     * @param {integer} n Total versions
     * @return {integer} The first bad version
     */
    return function(n) {
        let start = 1;
        let end = n;
        let mid = 0;

        while(start <= end) {
            mid = Math.floor((start + end) / 2);

            if(isBadVersion(mid)) {
                if(isBadVersion(mid - 1)) end = mid - 1;  // if mid bad is not *first* bad version, set end point to mid - 1
                else return mid;  // if mid is first bad version
            }
            else start = mid + 1; // if mid is not bad version, set start point to mid + 1
        }
    };
};
```

