class Solution extends VersionControl {
    /**
     * @param Integer $n
     * @return Integer
     */
    function firstBadVersion($n) {
        $left = 1;
        $right = $n;
        $current = false;
        while($left != $right){
            $current = (int)(($left + $right) / 2);
      if ($this->isBadVersion($current)) {
                $right = $current;
            } else {
                $left = $current + 1;
            }
        }
        return $right;

    }
}
