public class Solution extends VersionControl {

    public int versionSearch(int n, int left)
    {

        int mid=(left+n)/2;
        if(left>n) return 1;
        if(!isBadVersion(mid))
        {
            return versionSearch(n, mid+1);
        }
        else if(isBadVersion(mid)&&!isBadVersion(mid-1))
        {
            return mid;
        }else
        {
            return versionSearch(mid-1,left);
        }
    }
    public int firstBadVersion(int n) {
        return versionSearch(n, 1);
    }
