# Union Find
```
class UnionFind {
private:
    vector<int> parent, compSize, fishes;

public:
    UnionFind(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size(), mn = m * n;
        parent.resize(mn);
        compSize.resize(mn, 1);
        fishes.resize(mn);
        iota(parent.begin(), parent.end(), 0);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                fishes[n * i + j] = grid[i][j];
            }
        }
    }

    int getParent(int x) {
        if (x == parent[x]) {
            return x;
        }
        return parent[x] = getParent(parent[x]);
    }

    void unionSet(int x, int y) {
        int parx = getParent(x), pary = getParent(y);
        if (parx != pary) {
            if (compSize[parx] < compSize[pary]) {
                swap(parx, pary);
            }
            parent[pary] = parx;
            compSize[parx] += compSize[pary];
            fishes[parx] += fishes[pary];
        }
    }

    int getFishes(int x) {
        return fishes[getParent(x)];
    }
};

class Solution {
public:
    int findMaxFish(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size(), result = 0;
        vector<int> dr{0, 0, 1, -1}, dc{1, -1, 0, 0};
        UnionFind dsu(grid);
        for (int r = 0; r < m; r++) {
            for (int c = 0; c < n; c++) {
                if (grid[r][c]) {
                    for (int k = 0; k < 4; k++) {
                        int r1 = r + dr[k], c1 = c + dc[k];
                        if (0 <= r1 && r1 < m && 0 <= c1 && c1 < n && grid[r1][c1]) {
                            dsu.unionSet(n * r + c, n * r1 + c1);
                        }
                    }
                    result = max(result, dsu.getFishes(n * r + c));
                }
            }
        }
        return result;
    }
};
```


# Complexity
- Time complexity: $O(mn)$
- Space complexity: $O(mn)$

