class Solution {
public:

    int solve(vector<vector<int>>& grid, vector<vector<bool>>& visited, int r, int c) {
        visited[r][c] = true;
        int  cnt = grid[r][c], n = grid.size(), m = grid[0].size();

        if(c+1<m && grid[r][c+1] && !visited[r][c+1])   {
            cnt += solve(grid, visited, r, c+1);
        }

        if(c-1>=0 && grid[r][c-1] && !visited[r][c-1])  {
            cnt += solve(grid, visited, r, c-1);
        }

        if(r+1<n && grid[r+1][c] && !visited[r+1][c])   {
            cnt += solve(grid, visited, r+1, c);
        }

        if(r-1>=0 && grid[r-1][c] && !visited[r-1][c])  {
            cnt += solve(grid, visited, r-1, c);
        }

        return cnt;
    }

    int findMaxFish(vector<vector<int>>& grid) {
        int maxi = 0, n = grid.size(), m = grid[0].size();
        vector<vector<bool>> visited(n, vector<bool>(m, false));

        for(int i=0; i<n; i++)  for(int j=0; j<m; j++) 
                // if it is water cell and not visited the call the dfs function to calculate the total fish and compare with the maximum
                if(grid[i][j] && !visited[i][j])   {
                    int x = solve(grid, visited, i,  j);
                    maxi = max(maxi, x);
                }
        return maxi;
    }
};
