        for(int next = 0;next<4;next++){
            int x1 = x + dx[next];
            int y1 = y + dy[next];
            if(x1>=0 and y1>=0 and y1<grid[0].size() and x1<grid.size() and grid[x1][y1]!=0){
                *ans += grid[x1][y1];
                grid[x1][y1] = 0;
                dfs(x1,y1,grid,ans);
