    void solve(int i,int j,int &a,int &b,int &sum,int &ans,vector<vector<int>> &g){
        ans = max(ans,sum);
        if(i<0||j<0||i>=a||j>=b||g[i][j]==0){
            return;
        }
        sum += g[i][j];
        g[i][j] = 0;
        solve(i+1,j,a,b,sum,ans,g);
        solve(i,j+1,a,b,sum,ans,g);
        solve(i-1,j,a,b,sum,ans,g);
        solve(i,j-1,a,b,sum,ans,g);
