        vis[row][col]=1;
        cnt+=grid[row][col];

    }*/
    int findMaxFish(vector<vector<int>>& grid) {
        int cnt = 0;
        int m = grid.size();
        int n = grid[0].size();
vector<vector<int>>vis;
        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < n; j++)
            {
                if (grid[i][j] > 0)
                {
                    queue<pair<int, int>> q;
                    q.push({i, j});
                    int fish = grid[i][j];
                    grid[i][j] = 0;

                    while (!q.empty())
                    {
                        int row = q.front().first;
                        int col = q.front().second;
                        q.pop();

                        if (row > 0 && grid[row - 1][col] > 0)
                        {
                            fish += grid[row - 1][col];
                            q.push({row - 1, col});
                            grid[row - 1][col] = 0;
                        }
                        if (row < m - 1 && grid[row + 1][col] > 0)
                        {
                            fish += grid[row + 1][col];
                            q.push({row + 1, col});
                            grid[row + 1][col] = 0;
                        }
                        if (col > 0 && grid[row][col - 1] > 0)
                        {
                            fish += grid[row][col - 1];
                            q.push({row, col - 1});
                            grid[row][col - 1] = 0;
                        }
                        if (col < n - 1 && grid[row][col + 1] > 0)
                        {
                            fish += grid[row][col + 1];
                            q.push({row, col + 1});
                            grid[row][col + 1] = 0;
                        }
                    }

                    cnt = max(cnt, fish);
                              grid[i][j] = fish;
                }
            }
        }

        return cnt;
    }
};
