class Solution
{
public:
    bool check(int row, int col, int n, int m)
    {
        if (row >= 0 && row < n && col >= 0 && col < m)
        {
            return true;
        }
        return false;
    }

    int dfs(vector<vector<int>> &grid, int row, int col, vector<vector<bool>> &visited)
    {
        int n = grid.size();
        int m = grid[0].size();

        if (!check(row, col, n, m) || grid[row][col] == 0 || visited[row][col])
        {
            return 0;
        }

        visited[row][col] = true;
        int curr_fish = grid[row][col];
        int dir[4][2] = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

        for(int i = 0; i < 4; i++)
        {
            int newR = row + dir[i][0];
            int newC = col + dir[i][1];
            curr_fish += dfs(grid, newR, newC, visited);
        }

        return curr_fish;
    }

    int findMaxFish(vector<vector<int>> &grid)
    {
        int max_fish = 0;
        int n = grid.size();
        int m = grid[0].size();

        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++)
            {
                if(grid[i][j] > 0)
                {
                    vector<vector<bool>> visited(n, vector<bool>(m, false));
                    int curr_fish_now = dfs(grid, i, j, visited);
                    max_fish = max(curr_fish_now, max_fish);
                }
            }
        }

        return max_fish;
    }
};

