class Solution {
    public int findMaxFish(int[][] grid) {

        int n=grid.length;
        int m=grid[0].length;

        int visit[][]=new int[n][m];
        int delRow[]={0,0,-1,1};
        int delCol[]={1,-1,0,0};

        int ans[]={0};
        int max=0;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(visit[i][j]==0 && grid[i][j]!=0){

                    bfs(i,j,grid,visit,ans,delRow,delCol);
                    max=Math.max(max,ans[0]);
                    ans[0]=0;
                }
            }
        }
        return max;
