    public void bfs(int i,int j,int[][] grid,int visit[][],int ans[],int delRow[],int delCol[]){
        int n=grid.length;
        int m=grid[0].length;

        Queue<Pair>q=new LinkedList<>();
        q.add(new Pair(i,j));
        visit[i][j]=1;

        while(!q.isEmpty()){
