class Solution {
    public int findMaxFish(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        int maxFish = 0;
        boolean [][]vis = new boolean[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] != 0) { 
                    maxFish = Math.max(maxFish, dfs(i, j,grid));
                }
            }
        }
        return maxFish;
    }
    private int dfs(int i, int j, int[][] grid) {


        int m = grid.length;
        int n = grid[0].length;
        if(i<0 || i >= m || j < 0 || j >= n || grid[i][j] == 0){
            return 0;
        }
        int fish = grid[i][j]; 
        grid[i][j] = 0;
        fish += dfs(i-1, j, grid);
        fish += dfs(i, j+1, grid);
        fish += dfs(i+1, j, grid);
        fish += dfs(i, j-1, grid);
        return fish;
    }
}
