class Solution {
public:
    //x = exchange(a, b) --> x gets assigned with a & a is assiigned with b 
    int f(int i, int j, vector<vector<int>>& grid) {
        if(i < 0 || j < 0 || i >= grid.size() || j >= grid[0].size()) {
            return 0;
        }
        if(grid[i][j] == 0) {
            return 0;
        }

        return exchange(grid[i][j], 0) + f(i - 1, j, grid) + f(i, j - 1, grid) + f(i + 1, j, grid) + f(i, j + 1, grid);
    }
    int findMaxFish(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int maxi = 0;

        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(grid[i][j] > 0) {
                    maxi = max(maxi, f(i, j, grid));
                }
            }
        }

        return maxi;
    }
};
