class Solution {
public:
    int findMaxFish(vector<vector<int>>& grid) {
        int n = size(grid),m = size(grid[0]), fish = 0, t = 0;
        vector<vector<bool>> vis(n,vector<bool>(m));
        auto isvalid = [&](int i,int j){return i>=0 && i<n && j>=0 && j<m;};
        vector<pair<int,int>> moves{{0,1},{0,-1},{1,0},{-1,0}};
        function<void(int,int)> dfs = [&](int i,int j){
            t += grid[i][j];
            vis[i][j] = true;
            for(auto&[u,v]: moves){
                int x = i + u, y = j + v;
                if(isvalid(x,y) && grid[x][y] && !vis[x][y])
                    dfs(x,y);
            }
        };
        for(int i=0; i<n; ++i){
            for(int j=0; j<m; ++j){
                if(grid[i][j] && !vis[i][j]){
                    t = 0;
                    dfs(i,j);
                    fish = max(fish,t);
                }
            }
        }
        return fish;
    }
};
