class Solution {
public:
    int c=0;
    void rec(int i, int j, int n, int m, vector<vector<int>> &grid, vector<vector<int>> &vis){
        vis[i][j]=0;

        int dr[4]={-1, 0, 1, 0};
        int dc[4]={0, -1, 0, 1};

        for(int k=0; k<4; k++){
            int nr= dr[k]+i;
            int nc= dc[k]+j;

            if(nr>=0 && nc>=0 && nr<n && nc<m && grid[nr][nc]>0 && vis[nr][nc]==-1){
                c+=grid[nr][nc];
                rec(nr, nc, n, m, grid, vis);
            }
        }
    }

    int findMaxFish(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int maxi=0;
        vector<vector<int>> vis(n, vector<int>(m, -1));

        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                 c=0;
                if(grid[i][j]>0 && vis[i][j]==-1){
                    c+=grid[i][j];
                    rec(i, j, n, m, grid, vis);
                    maxi= max(maxi, c);
                }
            }
        }
        return maxi;
    }
};
