     {
         int fish=0;
         queue<pair<int,int>> q;
         q.push({i,j});
         while(!q.empty())
         {
             int x=q.front().first;
             int y=q.front().second;
             q.pop();
             fish+=grid[x][y];
             grid[x][y]=0;
             for(int k=0;k<4;k++)
