    int dfs(vector <vector<int>> &grid, vector<vector<int>> &visited, int row, int col)
    {
        visited[row][col] = 1;
        int prerow[] = {-1, 0, 1, 0};
        int precol[] = {0, -1, 0, 1};
        int res = grid[row][col];
        for(int i = 0; i < 4; i++)
        {
            int nrow = row + prerow[i];
            int ncol = col + precol[i];
            if(nrow >= 0 && nrow < grid.size() && ncol >= 0 && ncol < grid[0].size() && visited[nrow][ncol] == 0 && grid[nrow][ncol] > 0)
            {
                res += dfs(grid, visited, nrow, ncol);
            }
        }
        return res;
