        vector <vector <int>> visited(grid.size(), vector <int>(grid[0].size(), 0));
        int res = 0;
        for(int i = 0; i < grid.size(); i++)
        {
            for(int j = 0; j < grid[0].size(); j++)
            {
                if(!visited[i][j] && grid[i][j] > 0)
                {
                    res = max(res, dfs(grid, visited, i, j));
                }
            }
        }
        return res;
    }
};
