class Solution {

    int dirs1[]=new int[]{-1,0,1,0};
    int dirs2[]=new int[]{0,1,0,-1};

    int max = 0;
    int mn =0;
    int nn=0;

    public int dfs(int i,int j,int[][] grid,boolean vis[][]){
        if(i<0 || j<0 || i>= mn || j>= nn || vis[i][j] || grid[i][j]==0){
            return 0;
        }

        vis[i][j]=true;
        int neighborSum = 0;
        for(int k=0;k<4;k++){
            int r = i + dirs1[k];
            int c = j + dirs2[k];
            neighborSum += dfs(r,c,grid,vis);
        }
        return grid[i][j] + neighborSum;

    }
    public int findMaxFish(int[][] grid) {
        mn= grid.length;
        nn=grid[0].length;


        for(int i=0;i<mn;i++){
            for(int j=0;j<nn;j++){
                if(grid[i][j]!=0){
                    int size = dfs(i,j,grid,new boolean[mn][nn]);
                    max = Math.max(size,max);
                }
            }
        }
        return max;
    }
}

